SELECT t1.name as "Name",t1.marks1 as 'Subject 1', RANK() OVER (ORDER BY t1.marks1 DESC,t1.marks1+t1.marks2+t1.marks3+t1.marks4 DESC) AS "Rank" FROM marks t1 WHERE t1.marks1 > (t1.marks1 * 0.6) AND t1.marks2 > (t1.marks2 * 0.6) AND t1.marks3 > (t1.marks3 * 0.6) AND t1.marks4 > (t1.marks4 * 0.6)  LIMIT 3 ;
SELECT t2.name as "Name",t2.marks2 as 'Subject 2', RANK() OVER (ORDER BY t2.marks2 DESC,t2.marks1+t2.marks2+t2.marks3+t2.marks4 DESC) AS "Rank" FROM marks t2 WHERE t2.marks1 > (t2.marks1 * 0.6) AND t2.marks2 > (t2.marks2 * 0.6) AND t2.marks3 > (t2.marks3 * 0.6) AND t2.marks4 > (t2.marks4 * 0.6)  LIMIT 3 ;
SELECT t3.name as "Name",t3.marks3 as 'Subject 3', RANK() OVER (ORDER BY t3.marks3 DESC,t3.marks1+t3.marks2+t3.marks3+t3.marks4 DESC) AS "Rank" FROM marks t3 WHERE t3.marks1 > (t3.marks1 * 0.6) AND t3.marks2 > (t3.marks2 * 0.6) AND t3.marks3 > (t3.marks3 * 0.6) AND t3.marks4 > (t3.marks4 * 0.6)  LIMIT 3 ;
SELECT t4.name as "Name",t4.marks4 as 'Subject 4', RANK() OVER (ORDER BY t4.marks4 DESC,t4.marks1+t4.marks2+t4.marks3+t4.marks4 DESC) AS "Rank" FROM marks t4 WHERE t4.marks1 > (t4.marks1 * 0.6) AND t4.marks2 > (t4.marks2 * 0.6) AND t4.marks3 > (t4.marks3 * 0.6) AND t4.marks4 > (t4.marks4 * 0.6)  LIMIT 3 ;